import { Text, View, StyleSheet } from 'react-native'

const App = () => {
  return (<View>
    <Text style={styles.colorSty}>你好， react native
      {/* 内层样式继承外层样式 父子嵌套 */}
      <Text>is mine</Text>
    </Text>
    <Text style={[styles.colorSty, styles.secondLine]}>这是react native 第二行</Text>
  </View>)
}

const styles = StyleSheet.create({
  colorSty: {
    color: "#666",
    width: '100vw',
    display: 'inline-block',
    backgroundColor: '#ccc',
    textAlign: 'center',
    fontSize: 20
  },
  secondLine: {
    fontSize: 15,
    color: 'salmon',
  }
})

export default App;
